import { useQuery } from "react-query";
import { GraphQLClient, request } from "graphql-request";
import { GET_USER, GET_FINISHED_PROJECTS, GET_DATE_AND_XP, GET_LEVEL } from '../other/queries';


let projectsData
let userProfile
let tasks
let levels

export function magic() {
    userProfile = useGQLQuery('user', GET_USER);
    tasks = useGQLQuery('task', GET_FINISHED_PROJECTS);
    projectsData = useGQLQuery('xp', GET_DATE_AND_XP);
    levels = useGQLQuery('level', GET_LEVEL);

    if (userProfile.isLoading || tasks.isLoading || projectsData.isLoading || levels.isLoading) return "Loading...";

}

export const useGQLQuery = (key, query, variables) => {
    const endpoint = 'https://01.kood.tech/api/graphql-engine/v1/graphql';

    const fetchData = async () => await request(endpoint, query, variables);
    /*     if (userProfile.isLoading || tasks.isLoading || experiencePoints.isLoading || levels.isLoading) return "Loading...";
     */

    return useQuery([...key], fetchData)
}
