import React from 'react'
import { useRef, useEffect } from 'react';
import * as d3 from 'd3'

function XPGraph({ filterAudits }) {

    let myRef = useRef()
    myRef.current = filterAudits.sort((a, b) => new Date(a.createdAt) - new Date(b.createdAt));
    console.log(myRef.current)

    const svgRef = useRef()

    useEffect(() => {
        // setting up svg
        const width = 800;
        const height = 400;
        const margin = { top: 50, bottom: 50, left: 50, right: 50 }
        const Y = d3.map(myRef.current, d => d.amount);
        const X = d3.map(myRef.current, d => d.createdAt)
        console.log(Y)


        const svg = d3.select(svgRef.current)
            .attr('height', height - margin.top - margin.bottom)
            .attr('width', width - margin.left - margin.right)
            .attr('viewBox', [0, 0, width, height])

        // setting up the scaling
        const xScale = d3.scaleBand()
            .domain(d3.range(myRef.current.length))
            .range([margin.left, width - margin.right])
            .padding(0.1)


        const yScale = d3.scaleLinear()
            .domain([0, d3.max(Y)])
            .range([height - margin.bottom, margin.top])

        const scaleLine = d3.line()
            .x((d, i) => xScale(i))
            .y(yScale)
            .curve(d3.curveCardinal)

        svg
            .selectAll('.line')
            .data(Y)
            .join('path')
            .attr('d', d => scaleLine(d))
            .attr('fill', 'none')
            .attr('stroke', 'black')

        function yAxis(g) {
            g.attr('transform', `translate(${margin.left}, 0)`)
            g.call(d3.axisLeft(yScale))
        }

        function xAxis(g) {
            g.attr('transform', `translate(0, ${height - margin.bottom})`)
                .call(d3.axisBottom(xScale).tickFormat(i => X[i]))
        }

        svg.append('g').call(yAxis)
        svg.append('g').call(xAxis)


    }, myRef.current)


    return (
        <div className='line-chart'>
            <svg ref={svgRef}></svg>
        </div>
    )
}

export default XPGraph
