import React from 'react'
import { useGQLQuery } from '../other/useGQLQurery'
import { GET_USER, GET_FINISHED_PROJECTS, GET_DATE_AND_XP, GET_LEVEL } from '../other/queries';


let correctPaths = [];
let projectsData
let middleman = [];

function removeAudits(correct, possible) {
    let finished = [];
    let names = [];
    let k = 0;

    for (let i = 0; i < correct.length; i++) {
        for (let j = 0; j < possible.length; j++) {
            if (correct[i] === possible[j]) {
                middleman.push(projectsData.data.user[0].transactions[j])
            }
        }
    }

    middleman.sort();
    middleman.reverse();
    for (let i = 0; i < middleman.length; i++) {
        let taskName1 = middleman[i].object.name;

        while (k < middleman.length) {
            let taskName2 = middleman[k].object.name

            if (taskName1 === taskName2) {
                if (middleman[i].amount > middleman[k].amount) {

                    if (!names.includes(taskName1)) {
                        names.push(taskName1)
                        finished.push(middleman[i])
                    }
                } else if (middleman[i].amount == middleman[k].amount) {

                    if (!names.includes(taskName1)) {
                        names.push(taskName1)
                        finished.push(middleman[i])
                    }
                }
            }
            k++
        }
        k = 0;
    }

    return finished
}


function ProfileInfo() {

    const userProfile = useGQLQuery('user', GET_USER);
    const tasks = useGQLQuery('task', GET_FINISHED_PROJECTS);
    projectsData = useGQLQuery('xp', GET_DATE_AND_XP);
    const levels = useGQLQuery('level', GET_LEVEL);
    //let totalXP = 0;


    /* if you don't use isLoading it is going to throw an error */
    if (userProfile.isLoading || tasks.isLoading || projectsData.isLoading || levels.isLoading) return "Loading...";
    // if (userProfile.error) return "An error has occurred: " + error.message;

    const addPaths = tasks.data.user[0].progresses.map(path => {
        let proovime = path.path
        correctPaths.push(proovime)
    })

    //console.log(correctPaths)
    correctPaths.map(element => {
        //console.log(element)
    })

    /* filter out audits */
    const possiblePaths = projectsData.data.user[0].transactions.map(project => {
        if (project.object.type === 'module') {
            middleman.push(project)
        }
        return JSON.parse(JSON.stringify(project.path))
    })

    //console.log(names)

    possiblePaths.map(element => {
        // console.log(element)
    })


    const filterAudits = removeAudits(correctPaths, possiblePaths);
    //console.log(filterAudits)

    return (
        <div className='profile-info'>
            <div className='profile-username-div'>
                <p>{userProfile.data.user[0].login}</p>
            </div>
            <div className='profile-current-level-div'>
                <p>Current level:</p>
                <p id='current-level'>{levels.data.transaction[0].amount}</p>
            </div>
            <div className='profile-xp-amount'>

            </div>
        </div>
    )
}

export default ProfileInfo
