import * as d3 from "d3";

export function barGraph(filterAudits) {
    const width = 800;
    const height = 400;
    // const margin = { top: 50, bottom: 50, left: 50, right: 50 }
    const Y = d3.map(filterAudits, d => d.amount);
    const X = d3.map(filterAudits, d => d.object.name)
    console.log(filterAudits)

    const svg = d3.select('.App')
        .append('svg')
        .attr('height', height)
        .attr('width', width)
        .attr('viewBox', [0, 0, width, height])


    const xScale = d3.scaleBand()
        .domain(d3.range(filterAudits.length))
        .range([margin.left, width - margin.right])
        .padding(0.1)


    const yScale = d3.scaleLinear()
        .domain([0, d3.max(Y)])
        .range([0, heiht])

    svg
        .append('g')
        .attr('fill', 'royalblue')
        .selectAll('rect')
        .data(Y)
        .join('rect')
        .attr('x', (d, i) => xScale(i))
        .attr('y', (d) => yScale(d))
        .attr('height', (d) => yScale(d))
        .attr('width', xScale.bandwidth())

    function yAxis(g) {
        g.attr('transform', `translate(${margin.left}, 0)`)
        g.call(d3.axisLeft(yScale))
    }

    function xAxis(g) {
        g.attr('transform', `translate(0, ${height}`)
        g.call(d3.axisBottom(xScale).tickFormat(i => X[i]))
    }



    svg.append('g').call(yAxis)
    svg.append('g').call(xAxis)
    svg.node()
}
