import * as d3 from "d3";

export function barGraph(filterAudits) {
    const width = 800;
    const height = 400;
    const margin = { top: 50, bottom: 50, left: 50, right: 50 }

    const svg = d3.select('.selectTheDiv')
        .append('svg')
        .attr('height', height - margin.top - margin.bottom)
        .attr('width', width - margin.left - margin.right)
        .attr('viewBox', [0, 0, width, height])

    /* x scale */
    const x = d3.scaleBand()
        .domain(d3.range('data.length(which I dont have yet)'))
        .range([margin.left, width = margin.right])
        .padding(0.1)

    /* y scale */
    const y = d3.scaleLinear()
        .domain('siia peab tulema xp somehow')
        .range([height - margin.bottom, margin.top])

    svg
        .append('g')
        .attr('fill', 'royalblue')
        .selectAll('rect')
        .data('data. mis läheb graafikusse')
        .join('rect')
        .attr('x')
        .attr('y')
        .attr('height')
        .attr('width')
}
