import { useQuery } from "react-query";
import { GraphQLClient, request } from "graphql-request";



export const userProfile = useGQLQuery('user', GET_USER);
export const tasks = useGQLQuery('task', GET_FINISHED_PROJECTS);
export const experiencePoints = useGQLQuery('xp', GET_DATE_AND_XP);
export const levels = useGQLQuery('level', GET_LEVEL);


export const useGQLQuery = (key, query, variables) => {
    const endpoint = 'https://01.kood.tech/api/graphql-engine/v1/graphql';

    const fetchData = async () => await request(endpoint, query, variables);
    /*     if (userProfile.isLoading || tasks.isLoading || experiencePoints.isLoading || levels.isLoading) return "Loading...";
     */

    return useQuery([...key], fetchData)
}
