import React from 'react'
import { useGQLQuery } from '../other/useGQLQurery'
import { GET_USER, GET_FINISHED_PROJECTS, GET_DATE_AND_XP, GET_LEVEL } from '../other/queries';

function ProfileInfo() {

    const userProfile = useGQLQuery('user', GET_USER);
    const tasks = useGQLQuery('task', GET_FINISHED_PROJECTS);
    const experiencePoints = useGQLQuery('xp', GET_DATE_AND_XP);
    const levels = useGQLQuery('level', GET_LEVEL);

    /* if you don't use isLoading it is going to throw an error */
    if (userProfile.isLoading || tasks.isLoading || experiencePoints.isLoading || levels.isLoading) return "Loading...";
    // if (userProfile.error) return "An error has occurred: " + error.message;

    console.log(tasks.data.user)
    const wtf = userProfile.data.user[0].login
    return (
        <div>
            <p>{wtf}</p>
        </div>
    )
}

export default ProfileInfo
