import React, { createRef, useEffect } from "react";
import { barGraph } from '../other/graphs.js'


function TaskGraph({ filterAudits }) {

    const myRef = createRef()


    filterAudits = filterAudits.sort((a, b) => (a.amount) - (b.amount))
    // console.log(filterAudits)

    useEffect(() => {
        myRef.current.innerHTML = ''
        let chart = barGraph(filterAudits)
        myRef.current.append(chart)
    }, [myRef])



    return (
        <div className='task-graph' ref={myRef}>

        </div>
    )
}

export default TaskGraph
