        correctPaths.push(proovime)
    })



    /* filter out audits */
    const possiblePaths = projectsData.data.user[0].transactions
    const projects = removeAudits(correctPaths, possiblePaths)
    console.log(projects)

    return (
        <div className='profile-info'>
